function register_user(){var d=document.getElementById("register-firstname").value;var c=document.getElementById("register-lastname").value;var b=document.getElementById("register-email").value;var a=document.getElementById("register-password").value;$.ajax({type:"POST",url:"scripts/register.php",data:{name_l:c,name_f:d,email:b,password:a},success:function(e){if(e==="success"){window.location.href="index.html";return false}else{alert(e)}},error:function(e){alert("Your internet needs to be checked. Try again later.")}});return false}function login_user(){var b=document.getElementById("login-email").value;var a=document.getElementById("login-password").value;$.ajax({type:"POST",url:"scripts/login.php",data:{email:b,password:a},success:function(c){if(c==="success"){window.location.href="index.html";return false}else{alert(c)}},error:function(c){alert("Your internet needs to be checked. Try again later.")}});return false};